html {
  font-size: 10px;
}

body {
  background-color: #FFFBFB;
  padding-left: 8%;
  padding-right: 8%;
  margin: 0px;
}

.Header {
  display: flex;
  flex-direction: row;
  justify-content: space space-between;
  align-items: center;
  width: 100%;
  background: linear-gradient(180deg, #FFFBFB 57.06%, rgba(255, 251, 251, 0) 93.48%);
  padding: 48px;
  margin-left: -5%;
}

.Header p:hover {
  color: #E6C8C4;
}

.left-link {
  text-decoration: none;
  margin-right: auto;
}

header nav {
  display: flex;
  text-decoration: none;
  justify-content: space-between;
  align-items: center;
}

header nav a {
  text-decoration: none;
  color: #333;
  margin: 0 10px;
}

.Intro {
  text-align: left;
}

.sticky-header {
  position: sticky;
  top: 0;
}

h1 {
  color: #000000;
  text-align: center;
  font-family: "Libre Baskerville", serif;
  font-weight: bold;
  font-size: 4rem;
}

h2 {
  color: #000000;
  font-family: "Libre Baskerville", serif;
  font-weight: bold;
  font-size: 2.6rem;
  margin-bottom: 1%;
}

h3 {
  font-family: "poppins", sans-serif;
  font-size: 1.6rem;
  font-weight: 100;
  line-height: 2.4rem;
  color: #000000;
  margin: 0px;
}

p {
  font-family: "poppins", sans-serif;
  font-size: 1.6rem;
  font-weight: 100;
  line-height: 2.4rem;
  color: #000000;
  margin: 0px;
}

.Italic {
  font-family: "poppins", sans-serif;
  font-size: 1.6rem;
  font-weight: 100;
  line-height: 2.4rem;
  font-style: italic;
  color: #000000;
  margin: 0%;
}

.Button {
  background-color: #C48178;
  color: #fff;
  border: none;
  padding: 8px 16px;
  margin: 20px;
  border-radius: 8px;
  cursor: pointer;
  text-decoration: none;
  font-family: "poppins", sans-serif;
  font-size: 1.1rem;
  font-weight: 100;
  line-height: 1.6rem;
  color: #ffffff;
}

.Button:hover {
  background-color: #E6C8C4;
}

.NoUnderline {
  text-decoration: none;
}

.max-width-imgtop {
  max-width: 100%;
}

.Start {
  text-align: center;
}

.container {
  display: flex;
  align-items: center;
  justify-content: center;
  text-align: center;
  max-width: 50%;
  margin-top: 2%;
}

.container2 {
  display: flex;
  align-items: left;
  justify-content: left;
  text-align: left;
  max-width: 100%;
  margin-top: 6%;
  margin-bottom: 2%;
}

.container3 {
  display: flex;
  align-items: center;
  justify-content: center;
  max-width: 100%;
  margin-top: 6%;
  margin-bottom: 2%;
}

.container4 {
  display: flex;
  flex-direction: column;
  align-items: left;
  max-width: 50%;
  margin: auto;
}

.text-container p {
  line-height: 1.5;
  margin-bottom: 20px;
}

.container .text {
  flex: 1;
  width: 50%;
  text-align: left;
}

.centered-h1 {
  font-size: 40px;
  margin-top: 5%;
  justify-content: center;
  text-align: center;
  width: 100%;
}

.image-container2 {
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 100%;
}

.item {
  text-align: center;
  margin: 3%;
}

.image-container {
  text-align: center;
}

.image-container img {
  max-width: 100%;
  height: auto;
  margin: 20px;
  float: right;
  margin-top: -6%;
  margin: 20%;
}

.flex-container img {
  margin: 8px;
}

.flex-container-left {
  flex-direction: row;
  margin-top: 100px;
  text-align: left;
}

.flex-container {
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
  margin: 0px;
}

.flex-container-rubrik {
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
  margin: 0px;
  margin-top: 180px;
}

.Ux-casestudies {
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
  align-content: space-between;
  margin: 8px;
  gap: 18px;
  line-height: 24px;
  color: #000000;
}

.wrap {
  display: flex;
  flex-direction: align;
}

.Horisontell {
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
}

.flex-container-text {
  display: flex;
  flex-direction: column;
  align-items: left;
  text-align: left;
  margin-top: 2%;
}

.persona-pic {
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
  margin: 0px;
  max-width: 100%;
  margin: 50px;
}

.tests-pic {
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
  margin: 0px;
  max-width: 100%;
  margin: 50px;
}

.pics {
  max-width: 60%;
}

.social-icons {
  display: flex;
  align-items: center;
  margin-bottom: 1px;
}

.max-width-img {
  max-width: 350px;
  height: auto;
}

.max-width-text {
  max-width: 379px;
}

.footer {
  margin-top: 200px;
  margin-bottom: 60px;
}

.footer-content {
  display: flex;
  align-items: center;
}

.footer-content a {
  text-decoration: none;
  color: #000000;
  margin: 0 10px;
}

.line {
  height: 0.5px;
  background-color: #000;
  margin-top: 70px;
  margin-bottom: 60px;
}

.footer-content p:hover {
  color: #E6C8C4;
}

.back-to-top {
  margin-top: 10px;
}

.resumeimg {
  margin-top: 130px;
  margin-bottom: 130px;
  padding: 20 px;
}

.Intro {
  text-align: left;
  width: 360px;
}

.MyButton {
  margin-top: 36px;
  margin-bottom: 36px;
  padding: 20 px;
}

@media (max-width: 768px) {
  img {
    width: 100%;
  }
}
@media (min-width: 993px) {
  img {
    width: 33%;
  }
}
@media (min-width: 769px) and (max-width: 992px) {
  img {
    width: 50%;
  }
}


